INSERT INTO `tblposts` VALUES("426", "1", "2020-02-01 22:26:52", "2020-02-02 03:26:52", "<!-- wp:paragraph -->\n<p>\n\nEl santo Bautismo es el fundamento de toda la vida cristiana, el pórtico de la vida en el espíritu (\"vitae spiritualis ianua\") y la puerta que abre el acceso a los otros sacramentos. Por el Bautismo somos liberados del pecado y regenerados como hijos de Dios, llegamos a ser miembros de Cristo y somos incorporados a la Iglesia y hechos partícipes de su misión (cf Concilio de Florencia: DS 1314; CIC, can 204,1; 849; CCEO 675,1): Baptismus est sacramentum regenerationis per aquam in verbo\" (\"El bautismo es el sacramento del nuevo nacimiento por el agua y la palabra\": Catecismo Romano 2,2,5).\n\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Requisitos (0 - 7 años)</h3>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>Registro civil del menor.</li><li>Partida de matrimonio, partida de bautizo o registro civil de los padres </li><li>Fotocopia de la cédula de los padres.</li><li>Partida de matrimonio o partida de confirmación de los padrinos (en el caso de que sean solteros)</li><li>Fotocopia de la cédula de los padrinos. </li><li>Datos de los abuelos maternos y paternos.</li><li>Se realizarán de acuerdo con la programación, el sábado 4 pm.</li><li>Se debe sentar la partida en la misma semana del bautismo.</li><li>El curso prebautismal será el viernes día anterior al bautismo a las 6:00 p.m. En el templo.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Requisitos (8 - 13 años) </h3>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>Registro civil del menor.</li><li>Partida de matrimonio, partida de bautizo o\nregistro civil de los padres </li><li>Fotocopia de la cedula de los padres.</li><li>Partida de matrimonio o partida de confirmación\nde los padrinos (en el caso de que sean solteros)</li><li>Fotocopia de la cedula de los padrinos. </li><li>Datos de los abuelos maternos y paternos.</li><li>Se realizarán de acuerdo con la programación</li><li>Declaración bajo juramento de los padres sobre\nla inexistencia de otro bautismo ante el párroco.</li><li>Si es mayor de 18 años se le toma declaración\njuramentada</li><li>Constancia de la debida preparación de los\nsacramentos.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3> Requisitos (14 - 17 años)  </h3>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>Registro civil reciente y autenticado.</li><li>Fotocopia de la cedula de los padres</li><li>Partida de matrimonio o partida de bautismo o registro civil de cada uno de los padres.</li><li>Declaración bajo juramento de los padres sobre la inexistencia de otro bautismo ante el párroco.</li><li>Se debe realizar la respectiva licencia de bautismo para mayores de 14 años; su valor es de $50.000. </li><li>Partida de matrimonio o partida de confirmación de los padrinos (en el caso de que sean solteros)</li><li>Fotocopia de la cédula de los padrinos. </li><li>Constancia de la debida preparación de los sacramentos.</li><li>Petición escrita del bautismo por parte del candidato.</li><li>Se debe sentar la partida en la misma semana del bautismo.</li><li>Nota: los documentos con máximo 3 meses de haber sido expedidos.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3> Requisitos (Mayores de 18 años)   </h3>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>Registro civil reciente y autenticado.</li><li>Fotocopia de la cedula de los padres</li><li>Partida de matrimonio o partida de bautismo o registro civil de cada uno de los padres.</li><li>Declaración bajo juramento de los padres sobre la inexistencia de otro bautismo ante el párroco.</li><li>Declaración bajo juramento sobre su soltería hecha por el candidato y un familiar cercano. </li><li>Se debe realizar la respectiva licencia de bautismo para mayores de 18 años.</li><li>Constancia de la debida preparación de los sacramentos.</li><li>Petición escrita del bautismo por parte del candidato.</li><li>Se debe sentar la partida en la misma semana del bautismo.</li><li>Nota: los documentos con máximo 3 meses de haber sido expedidos.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Fechas Disponibles</h3>\n<!-- /wp:heading -->\n\n<!-- wp:table {\"align\":\"center\",\"className\":\"is-style-stripes\"} -->\n<figure class=\"wp-block-table aligncenter is-style-stripes\"><table><tbody><tr><td><strong>Enero</strong></td><td>18</td></tr><tr><td><strong>Febrero</strong></td><td>15 y 29</td></tr><tr><td><strong>Marzo</strong></td><td>14 y 28</td></tr><tr><td><strong>Abril</strong></td><td>4 y 18</td></tr><tr><td><strong>Mayo</strong></td><td>16 y 30</td></tr><tr><td><strong>Junio</strong></td><td>13 y 27</td></tr><tr><td><strong>Julio</strong></td><td>11 y 25</td></tr><tr><td><strong>Agosto</strong></td><td>15 y 29</td></tr><tr><td><strong>Septiembre</strong></td><td>12 y 26</td></tr><tr><td><strong>Octubre</strong></td><td>10 y 24</td></tr><tr><td><strong>Noviembre</strong></td><td>14 y 28</td></tr><tr><td><strong>Diciembre</strong></td><td>Todos los sabados</td></tr></tbody></table></figure>\n<!-- /wp:table -->", "El sacramento del Bautismo", "", "inherit", "closed", "closed", "", "88-revision-v1", "", "", "2020-02-01 22:26:52", "2020-02-02 03:26:52", "", "88", "https://www.parroquialalaguna.com/index.php/88-revision-v1/", "0", "revision", "", "0");
INSERT INTO `tblposts` VALUES("427", "1", "2020-02-01 22:30:11", "2020-02-02 03:30:11", "<!-- wp:html -->\n<table class=\"wp-block-table aligncenter is-style-regular\" style=\"text-align:center;\"><tbody><tr><td><strong>Descripción</strong>                        </td><td><strong>Valor</strong>                        </td></tr><tr><td>Misa Comunitaria de 1 Intención\n                        </td><td>$6.000\n                        </td></tr><tr><td>Misa Comunitaria de 3 Intenciones\n                        </td><td>$15.000\n                        </td></tr><tr><td>Misa Unica\n                        </td><td>$65.000\n                        </td></tr><tr><td>Partidas de Bautismo, Confirmación, Matrimonio y Defunción\n                        </td><td>$12.500\n                        </td></tr><tr><td>Certificados Negativos\n                        </td><td>$10.500\n                        </td></tr><tr><td>Certificados de Solteria, Sacramentos, Curso Prebautismal y Otros\n                        </td><td>$10.500\n                        </td></tr><tr><td>Matrimonios\n                        </td><td>$165.000\n                        </td></tr><tr><td>Bautismos\n                        </td><td>$50.000\n                        </td></tr><tr><td>Exequias\n                        </td><td>$155.000\n                        </td></tr><tr><td>Licencias de Matrimonio\n                        </td><td>$135.000\n                        </td></tr><tr><td>Inscripciones para Primera Comunión o Confirmación\n                        </td><td>$50.000\n                        </td></tr><tr><td>Autenticación de Partidas <br>(Unicamente en la Diocesis de Fontibón de Lunes a Viernes de 9:00 a.m. a 1:00 p.m.)\n                        </td><td>$10.500\n                        </td></tr></tbody></table>\n<!-- /wp:html -->", "Costos", "", "inherit", "closed", "closed", "", "153-revision-v1", "", "", "2020-02-01 22:30:11", "2020-02-02 03:30:11", "", "153", "https://www.parroquialalaguna.co