INSERT INTO `tblposts` VALUES("455", "1", "2020-03-05 19:13:39", "2020-03-06 00:13:39", "<!-- wp:paragraph -->\n<p>Bienvenido!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Aquí encontraras todo el material necesario para desarrollar tu catequesis en este año.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>Prebautismal &nbsp;</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>Eucaristía y Reconciliación </h2>\n<!-- /wp:heading -->\n\n<!-- wp:file {\"id\":469,\"href\":\"https://www.parroquialalaguna.com/wp-content/uploads/2020/03/Hoja-de-vida-Eucaristia-y-Reconciliacion.pdf\"} -->\n<div class=\"wp-block-file\"><a href=\"https://www.parroquialalaguna.com/wp-content/uploads/2020/03/Hoja-de-vida-Eucaristia-y-Reconciliacion.pdf\" target=\"_blank\" rel=\"noreferrer noopener\">Hoja de vida para el sacramento de Eucaristía y Reconciliación</a><a href=\"https://www.parroquialalaguna.com/wp-content/uploads/2020/03/Hoja-de-vida-Eucaristia-y-Reconciliacion.pdf\" class=\"wp-block-file__button\" download>Descarga</a></div>\n<!-- /wp:file -->\n\n<!-- wp:file {\"id\":470,\"href\":\"https://www.parroquialalaguna.com/wp-content/uploads/2020/03/Diagnostico-para-Eucaristia-y-Reconciliacion.pdf\"} -->\n<div class=\"wp-block-file\"><a href=\"https://www.parroquialalaguna.com/wp-content/uploads/2020/03/Diagnostico-para-Eucaristia-y-Reconciliacion.pdf\" target=\"_blank\" rel=\"noreferrer noopener\">Diagnóstico para el sacramento de Eucaristía y Reconciliación</a><a href=\"https://www.parroquialalaguna.com/wp-content/uploads/2020/03/Diagnostico-para-Eucaristia-y-Reconciliacion.pdf\" class=\"wp-block-file__button\" download>Descarga</a></div>\n<!-- /wp:file -->\n\n<!-- wp:file {\"id\":494,\"href\":\"https://www.parroquialalaguna.com/wp-content/uploads/2020/03/1.-EL-AMOR-DE-DIOS-EUCARISTIA-Y-RECONC