INSERT INTO `tblwc_admin_notes` VALUES("1", "wc-admin-wc-helper-connection", "info", "en_US", "Conectar con WooCommerce.com", "Conéctate para recibir avisos y actualizaciones importantes de productos.", "info", "{}", "unactioned", "woocommerce-admin", "2020-03-16 19:18:35", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("2", "wc-admin-welcome-note", "info", "en_US", "Nueva(s) característica(s)", "¡Bienvenido a la nueva experiencia de WooCommerce! ¡En esta nueva versión podrás echar un vistazo a cómo funciona tu tienda en el escritorio, gestionar aspectos importantes de tu negocio (como la gestión de pedidos, inventario, valoraciones) desde cualquier parte de la interfaz, navegar en los datos de tu tienda con una sección de análisis completamente nueva y más!", "info", "{}", "unactioned", "woocommerce-admin", "2020-03-16 19:18:35", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("3", "wc-admin-add-first-product", "info", "en_US", "Añade tu primer producto", "Aumenta tus ingresos añadiendo productos a tu tienda. Añade productos manualmente, importa desde una hoja o migra desde otra plataforma.", "product", "{}", "unactioned", "woocommerce-admin", "2020-03-16 19:18:37", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("4", "wc-admin-onboarding-profiler-reminder", "update", "en_US", "¡Bienvenido a WooCommerce! Configura tu tienda y empieza a vender", "Estamos aquí para ayudarte a dar los pasos más importantes para poner tu tienda en marcha.", "info", "{}", "actioned", "woocommerce-admin", "2020-03-17 00:18:47", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("5", "wc-admin-store-notice-setting-moved", "info", "en_US", "Looking for the Store Notice setting?", "It can now be found in the Customizer.", "info", "{}", "actioned", "woocommerce-admin", "2020-04-04 06:19:07", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("6", "wc-admin-mobile-app", "info", "en_US", "Instala la aplicación móvil WooCommerce", "Instala la aplicación móvil de WooCommerce para gestionar pedidos, recibir avisos de ventas y ver métricas clave – allí donde estés.", "phone", "{}", "unactioned", "woocommerce-admin", "2020-04-06 19:32:16", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("7", "wc-admin-onboarding-email-marketing", "info", "en_US", "Trucos, actualizaciones de productos e inspiración", "Estamos aquí por ti - consigue trucos, actualizaciones de productos e inspiración en tu buzón.", "mail", "{}", "unactioned", "woocommerce-admin", "2020-04-06 19:32:17", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("8", "wc-admin-store-notice-giving-feedback", "info", "en_US", "Valora tu experiencia", "´Si te gusta WooCommerce Admin, por favor, déjanos una valoración de 5 estrellas. ¡Muchas gracias por adelantado!", "info", "{}", "unactioned", "woocommerce-admin", "2020-04-07 19:22:28", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("9", "wc-admin-facebook-extension", "info", "en_US", "Vende en Facebook", "Haz crecer tu negocio dirigiéndote a las personas adecuadas e impulsando las ventas con Facebook. Puedes instalar ahora esta extensión gratuita.", "thumbs-up", "{}", "unactioned", "woocommerce-admin", "2020-04-07 19:22:28", NULL, "0");
INSERT INTO `tblwc_admin_notes` VALUES("10", "wc-admin-personalize-store", "info", "en_US", "Personaliza la página de inicio de tu tienda", "La página de inicio es uno de los puntos de entrada más importantes de tu tienda. Cuando se hace bien, puede dirigir a mayores conversiones y participación. No olvides personalizar la página de inicio que hemos creado para tu tienda durante la integración.", "notice", "{}", "unactioned", "woocommerce-admin", "2020-05-11 19:20:09", NULL, "0");
